$.fn.fadelist=function(e){return this.each(function(){var t={show:5,fade:1500};var n=$.extend(t,e),r=this,i=0,s=$(this),o=s.children("li").not(".hideItem"),u=o.length,a=parseInt(n.show);if(r.loop){clearInterval(r.loop)}var f=function(){for(var e=i;e<i+a;e++){o.eq(e).fadeOut(n.fade,function(){o.slice(i,i+a).fadeIn(n.fade)})}i+=a;if(i>=u)i=0};var l=function(){o.show();s.children("li:gt("+(a-1)+")").not(".hideItem").hide();if(u>a){r.loop=setInterval(function(){f()},5e3)}};l()})}
